import React from "react";
import { Box, Divider, Typography } from "@mui/material";
import UserImg from "components/UserImg";
import PersonAddIcon from "@mui/icons-material/PersonAdd";

const UserWidget = () => {
  const addFriend = () => {
    alert("add");
  };
  return (
    <>
      {/* pic,  username+friends num, icon that links to profile page */}
      <Box display="flex" width="100vw" height="100vh" pt="100px">
        <UserImg />
        <Box>
          <Typography>userName</Typography>
          <Typography>_ friends</Typography>
        </Box>
        <PersonAddIcon onClick={addFriend} />
      </Box>
      <hr style={{ borderTop: "1px solid grey", margin: "" }} />

      {/* location  and business */}
      <Box></Box>
      {/* Who's visited  your profile(neceesary?) */}
      <Box></Box>
      {/* Social Profiles, add Twitter and  LinkedIn */}
      <Box></Box>
    </>
  );
};

export default UserWidget;
