import { Box, Typography } from "@mui/material";
import FlexBetween from "components/FlexBetween";
import WidgetContainer from "components/WidgetContainer";
import PersonRemoveIcon from "@mui/icons-material/PersonRemove";
import React from "react";
import { setFriends, store } from "state";

const FriendList = () => {
  const addFriend = () => {
    store.dispatch(setFriends({ name: "jack" }));
    console.log(store.getState());
    // save user info (name etc.) in state, use that state in friendlist widgets
  };
  return (
    <WidgetContainer>
      <Typography variant="subtitle2" sx={"fontWeight:500px"} pb="10px">
        Friend List
      </Typography>
      <FlexBetween>
        {/* friend pic, name, and job*/}
        <Box height={"1.5rem"}>
          <img src="" height={"100%"} alt="pic" />
          <Box>
            <Typography variant="body" sx={"fontWeight:500px"}>
              name
            </Typography>
            <Typography variant="body" color="grey">
              job
            </Typography>
          </Box>
        </Box>
        <PersonRemoveIcon
          onClick={addFriend}
          sx={{
            background: "hsl(200, 53%, 90%)",
            color: "hsl(210, 80%, 65%)",
            fontSize: ".8rem",
            padding: "5px",
            boxShadow: "5px",
            borderRadius: "1rem",
            cursor: "pointer",
          }}
        />
      </FlexBetween>
    </WidgetContainer>
  );
};

export default FriendList;
