import useState from "react";
import { Navigate } from "react-router-dom";
import { useMediaQuery, Box, Typography } from "@mui/material";
import Form from "./Form";

const LoginPage = (isLargeScre) => {
  const beige = "#F3F3F3";
  

  return (
    <Box bgcolor={beige}>
      <Box
        display="flex"
        justifyContent="center"
        width="100vw"
        height="100vh"
        pt="100px"
      >
        <Box
          textAlign="center"
          maxWidth="80%"
          bgcolor="white"
          width="50vw"
          maxHeight="240px"
          fontSize=".8rem"
        >
          <Typography
            variant="h6"
            fontWeight="400"
            pt="1rem"
            textAlign="start"
            px="2rem"
          >
            Welcome to FriendSphere!
          </Typography>
          <Form />
        </Box>
      </Box>
    </Box>
  );
};

export default LoginPage;
