import { Box, Button, Divider, TextField } from "@mui/material";
import FlexBetween from "components/FlexBetween";
import UserImg from "components/UserImg";
import WidgetContainer from "components/WidgetContainer";
import React from "react";

const Post = () => {
  const handlePost = () => {
    // send props to the posts widget
  };
  return (
    <Box>
      <WidgetContainer>
        <FlexBetween>
          <UserImg />
          <TextField
            sx={{
              height: "2rem",
              borderRadius: ".5rem",
              overflow: "hidden",
              background: "#F3F3F3",
              borderRadius: "1rem",
            }}
            InputProps={{
              style: { fontSize: ".8rem", padding: ".8rem" },
            }}
            variant="standard"
          />
        </FlexBetween>
        <Divider />
        <FlexBetween>
          <></>
          <Button
            onClick={handlePost}
            variant="contained"
            sx={{ borderRadius: "1rem", fontSize: ".5rem" }}
          >
            POST
          </Button>
        </FlexBetween>
      </WidgetContainer>
    </Box>
  );
};

export default Post;
