import WidgetContainer from "components/WidgetContainer";
import FlexBetween from "components/FlexBetween";
import React from "react";
import { Box } from "@mui/material";

const Advert = () => {
  const adverts = [
    {
      company: "Swift Motors",
      url: "www.swiftmotors.com",
      description:
        "Get the best deals on brand new cars at Swift Motors! We have a wide selection of vehicles from top brands, and our financing options make it easy to drive off the lot in your dream car. Visit us today at www.swiftmotors.com.",
    },
    {
      company: "Green Thumbs Landscaping",
      url: "www.greenthumbs.com",
      description:
        "Transform your outdoor space into a beautiful oasis with Green Thumbs Landscaping. Our team of experts can design and install custom landscapes that will enhance the beauty of your home or business. Visit us today at www.greenthumbs.com to schedule a consultation.",
    },
    {
      company: "Tech Savvy Solutions",
      url: "www.techsavvysolutions.com",
      description:
        "Need help with your technology? Look no further than Tech Savvy Solutions! Our team of IT experts can assist with everything from computer repair to network setup and security. Visit us at www.techsavvysolutions.com to learn more about our services and to schedule a consultation.",
    },
  ];
  const randomAdvert = adverts[Math.floor(Math.random() * adverts.length)];

  return (
    <WidgetContainer>
      <Box>Sponsored</Box>
      <Box>
        <img src="" alt="advert" />
      </Box>
      <FlexBetween>
        <Box>{randomAdvert.company}</Box>
        <Box>{randomAdvert.url}</Box>
      </FlexBetween>
      <Box>{randomAdvert.url}</Box>
    </WidgetContainer>
  );
};

export default Advert;
