import { useState } from "react";

import {
  Search,
  Message,
  Notifications,
  Help,
  MoreVert,
} from "@mui/icons-material";

import {
  IconButton,
  AppBar,
  Toolbar,
  Typography,
  TextField,
  InputAdornment,
  Box,
  Menu,
  MenuItem,
} from "@mui/material";

const Navbar = () => {
  const black = "#292e36";
  const beige = "#F3F3F3";
  const handleClose = () => {};
  const isMenuOpen = () => {
    return true;
  };
  return (
    <>
      <AppBar>
        {/* Mobile Navbar */}

        {/* Desktop Navbar */}
        <Toolbar
          sx={{
            bgcolor: "white",
            display: "flex",
            justifyContent: "space-between",
            flexWrap: "nowrap",
          }}
        >
          <Box sx={{ display: "flex", alignItems: "center" }}>
            <Typography
              variant="h6"
              sx={{ color: "#42a5f5" }}
              fontWeight="bold"
            >
              FriendSphere
            </Typography>
            <TextField
              sx={{
                margin: ".5rem",
                fontSize: "1rem",
                borderRadius: ".5rem",
                overflow: "hidden",
                background: beige,
              }}
              variant="standard"
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Search sx={{ color: black }} />
                  </InputAdornment>
                ),
              }}
            />
          </Box>
          <Box sx={{ display: "flex" }}>
            <Message
              sx={{ fontSize: "1.5rem", color: black, paddingTop: "2px" }}
            />
            <Notifications
              sx={{ fontSize: "1.5rem", color: black, paddingX: ".7rem" }}
            />
            <Help sx={{ fontSize: "1.5rem", color: black }} />
            {/* <MoreVert sx={{ fontSize: "1.5rem", color: black }} /> */}
          </Box>
          <Menu
            id="menu"
            // anchorEl={anchorEl}
            open={isMenuOpen()}
            // onClose={handleClose}
            keepMounted
          >
            <IconButton onClick={handleClose}>
              <MoreVert sx={{ fontSize: "1.5rem", color: black }} />
            </IconButton>
            <MenuItem onClick={handleClose}>Profile</MenuItem>
            <MenuItem onClick={handleClose}>My account</MenuItem>
            <MenuItem onClick={handleClose}>Logout</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>
    </>
  );
};

export default Navbar;
