import useState from "react";
import { Navigate } from "react-router-dom";
import { Box, Typography, Link, Button } from "@mui/material";
import LoginForm from "./LoginForm";

const LoginPage = ({ isLargeScreen }) => {
  const beige = "#F3F3F3";
  const [toggleSignup, setToggleSignup] = useState(false);
  const [toggleLogin,setToggleLogin] = su
  // Once the form is submitted, handleLogin function is called to navigate to home page
  const handleLogin = () => {
    return <Navigate to="/home" />;
  };
  const handleToggleSignup = () => {
    setToggleSignup(true);
  };

  return (
    <Box bgcolor={beige}>
      <Box
        display="flex"
        justifyContent="center"
        width="100vw"
        height="100vh"
        pt="100px"
      >
        <Box
          textAlign="center"
          maxWidth="80%"
          bgcolor="white"
          width="50vw"
          maxHeight="280px"
          fontSize=".8rem"
        >
          <Typography
            variant={isLargeScreen ? "h6" : "body2"}
            fontWeight="500"
            pt="1rem"
            textAlign="start"
            px="2rem"
          >
            Welcome to FriendSphere!
          </Typography>
          <LoginForm />
          <Button onClick={handleToggleSignup}>
            Don't have an account? Sign up here
          </Button>
        </Box>
      </Box>
    </Box>
  );
};

export default LoginPage;
