import { Box, Button, Divider, TextField } from "@mui/material";
import FlexBetween from "components/FlexBetween";
import UserImg from "components/UserImg";
import WidgetContainer from "components/WidgetContainer";
import ImageIcon from "@mui/icons-material/Image";
import React, { useRef, useState } from "react";

const Post = () => {
  const [uploadImg, setUploadImg] = useState(null);
  const fileInputRef = useRef(null);

  const handlePost = () => {
    // send props to the posts widget
  };
  return (
    <Box>
    
      <WidgetContainer>
        <FlexBetween>
          <UserImg />
          <TextField
            sx={{
              height: "2rem",
              borderRadius: ".5rem",
              overflow: "hidden",
              background: "#F3F3F3",
              borderRadius: "1rem",
            }}
            InputProps={{
              style: { fontSize: ".8rem", padding: ".8rem" },
            }}
            variant="standard"
          />
        </FlexBetween>
        <Divider />
        <FlexBetween>
          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              cursor: "pointer",
            }}
            onClick={() => {
              fileInputRef.current.click();
            }}
          >
            <ImageIcon sx={{ fontSize: "1.2rem", color: "grey" }}></ImageIcon>
            Image
            <input
              type="file"
              hidden
              ref={fileInputRef}
              accept="image/*"
              onChange={(e) => {
                setUploadImg(e.target.files[0]);
              }}
            />
          </Box>
          <Button
            onClick={handlePost}
            variant="contained"
            sx={{ borderRadius: "1rem", fontSize: ".5rem" }}
          >
            POST
          </Button>
        </FlexBetween>
      </WidgetContainer>
    </Box>
  );
};

export default Post;
