import React, { useState } from "react";
import { Box, FormControl, Button, Input } from "@mui/material";

// Form handles form input and submission.
const Form = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    console.log("handleSubmit called");
    e.preventDefault();
    const formData = {
      email: email,
      password: password,
    };
    console.log(JSON.stringify(formData));
    // make AJAX request to server
    // fetch("/api/loginForm", {
    //   body: JSON.stringify(formData),
    //   method: "POST",
    // })
    //   .then((response) => {
    //     return response.json();
    //   })
    //   .then((data) => {
    //     console.log(data);
    //   })
    //   .catch((error) => {
    //     console.log(error);
    //   });
  };

  return (
<Box display="flex" flexDirection="column" px="2rem">
      <form onSubmit={handleSubmit}>
        <FormControl sx={{ "& input": { py: ".5rem" } }}>
          <Input
            type="text"
            disableUnderline={true}
            sx={{
              outline: "1px solid lightgrey",
              borderRadius: "5px",
              my: "1rem",
            }}
            placeholder=" Email"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <Input
            type="password"
            disableUnderline={true}
            sx={{ outline: "1px solid lightgrey", borderRadius: "5px" }}
            placeholder=" Password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
          <Button
            type="submit"
            variant="contained"
            sx={{ mt: "1rem", bgcolor: "#42a5f5" }}
          >
            Login
          </Button>
        </FormControl>
      </form>
    </Box>

    // 
  );
};

export default Form;
