import { useState } from "react";

import {
  Search,
  Message,
  Notifications,
  Help,
  MoreVert,
} from "@mui/icons-material";

import {
  IconButton,
  AppBar,
  Toolbar,
  Typography,
  TextField,
  InputAdornment,
  Box,
  Menu,
  MenuItem,
} from "@mui/material";

const Navbar = () => {
  const black = "#292e36";
  const beige = "#F3F3F3";
  const handleClose = () => {
    return false;
  };
  const isMenuOpen = () => {
    alert("click");
  };
  const [menuOpen, setMenuOpen] = useState(false);

  // test

  const handleMenuOpen = (event) => {
    setMenuOpen(true);
  };

  const handleMenuClose = () => {
    setMenuOpen(false);
  };

  const handleUserClick = () => {
    setUserOpen(!menuOpen);
  };

  return (
    <>
      <AppBar>
        {/* Mobile Navbar */}

        {/* Desktop Navbar */}
        <Toolbar
          sx={{
            bgcolor: "white",
            display: "flex",
            justifyContent: "space-between",
            flexWrap: "nowrap",
          }}
        >
          <Box sx={{ display: "flex", alignItems: "center" }}>
            <Typography
              variant="h6"
              sx={{ color: "#42a5f5" }}
              fontWeight="bold"
            >
              FriendSphere
            </Typography>
            <TextField
              sx={{
                margin: ".5rem",
                fontSize: "1rem",
                borderRadius: ".5rem",
                overflow: "hidden",
                background: beige,
              }}
              variant="standard"
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Search sx={{ color: black }} />
                  </InputAdornment>
                ),
              }}
            />
          </Box>
          <Box sx={{ display: "flex", flexWrap: "nowrap" }}>
            <Message
              sx={{ fontSize: "1.5rem", color: black, paddingTop: "2px" }}
            />
            <Notifications
              sx={{ fontSize: "1.5rem", color: black, paddingX: ".7rem" }}
            />
            <Help sx={{ fontSize: "1.5rem", color: black }} />
            {/* <Menu
              id="menu"
              open={true}
              // onClose={handleClose}
            >
              <IconButton onClick={handleClose}>
                <MoreVert
                  onClick={isMenuOpen}
                  sx={{ fontSize: "1.5rem", color: black }}
                />
              </IconButton>
              <MenuItem open={false} onClick={handleClose}>
                User
              </MenuItem>
              <MenuItem onClick={handleClose}>My account</MenuItem>
              <MenuItem onClick={handleClose}>Logout</MenuItem>
            </Menu> */}
            <IconButton onClick={handleMenuOpen}>
              <MoreVert sx={{ fontSize: "1.5rem", color: "black" }} />
            </IconButton>
            <Menu id="menu" open={menuOpen} onClose={handleMenuClose}>
              <MenuItem onClick={handleUserClick}>User</MenuItem>
              <MenuItem onClick={handleMenuClose}>My account</MenuItem>
              <MenuItem onClick={handleMenuClose}>Logout</MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </AppBar>
    </>
  );
};

export default Navbar;
