import { Box, Typography } from "@mui/material";
import FlexBetween from "components/FlexBetween";
import WidgetContainer from "components/WidgetContainer";
import PersonAddIcon from "@mui/icons-material/PersonAdd";
import React from "react";
import { setFriends, store } from "state";
import UserImg from "components/UserImg";

const FriendList = () => {
  const addFriend = () => {
    store.dispatch(setFriends({ name: "jack" }));
    console.log(store.getState());
    // save user info (name etc.) in state, use that state in friendlist widgets
  };
  return (
    <WidgetContainer>
      <Typography variant="subtitle2" sx={"fontWeight:500px"} pb="10px">
        Friend List
      </Typography>
      <FlexBetween>
        <Box height={"1.5rem"}>
          <UserImg></UserImg>

        </Box>
        <PersonRemoveIcon
          onClick={addFriend}
          sx={{
            background: "hsl(200, 53%, 90%)",
            color: "hsl(210, 80%, 65%)",
            fontSize: "1rem",
            padding: "3.5px",
            boxShadow: "5px",
            borderRadius: "1rem",
            cursor: "pointer",
          }}
        />
      </FlexBetween>
    </WidgetContainer>
  );
};

export default FriendList;
