import { useState } from "react";

import { Search, Notifications, Help } from "@mui/icons-material";
import {
  AppBar,
  Toolbar,
  Typography,
  TextField,
  InputAdornment,
} from "@mui/material";

const Navbar = () => {
  return (
    <>
      <AppBar>
        <Toolbar
          sx={{
            bgcolor: "white",
            display: "flex",
            justifyContent: "space-between",
          }}
        >
          <>
            <Typography
              variant="h6"
              sx={{ color: "#42a5f5" }}
              fontWeight="bold"
            >
              FriendSphere
            </Typography>
            <TextField
              sx={{ margin: ".5rem", fontSize: "1.5rem" }}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </>
          <Notifications sx={{ fontSize: "1.5rem",}} />
          <Help sx={{ fontSize: "1.5rem" }} />
        </Toolbar>
      </AppBar>
    </>
  );
};

export default Navbar;
