import { Box } from "@mui/system";
import FlexBetween from "components/FlexBetween";
import UserImg from "components/UserImg";
import WidgetContainer from "components/WidgetContainer";
import PersonAddIcon from "@mui/icons-material/PersonAdd";
import React from "react";

const Posts = () => {
  const addFriend = () => {
    // save user info (name etc.) in redstate, use that state in friendlist widgets
  };

  return (
    <WidgetContainer>
      <FlexBetween>
        <Box display={"flex"}>
          <UserImg />
          <Box pl=".8rem">postUserName</Box>
        </Box>
        <PersonAddIcon
          onClick={{ addFriend }}
          sx={{
            background: "hsl(200, 53%, 90%)",
            color: "hsl(210, 80%, 65%)",
            fontSize: "1rem",
            padding: "3.5px",
            boxShadow: "5px",
            borderRadius: "1rem",
            cursor: "pointer",
          }}
        />
      </FlexBetween>
    </WidgetContainer>
  );
};

export default Posts;
